import React from 'react';
import { Todo } from '../types/todo';
import axios from 'axios';

interface TodoListProps {
    todos: Todo[];
    onDelete: (id: number) => void;
    onUpdate: (updated: Todo) => void;
}

const TodoList: React.FC<TodoListProps> = ({ todos, onDelete, onUpdate }) => {
    const handleDelete = (id: number) => {
        axios.delete(`http://localhost:8080/api/todos/${id}`)
            .then(() => onDelete(id))
            .catch(err => console.error("Error deleting todo:", err));
    };

    const handleToggleCompleted = (todo: Todo) => {
        const updatedTodo = { ...todo, completed: !todo.completed };
        axios.put(`http://localhost:8080/api/todos/${todo.id}`, updatedTodo)
            .then(res => onUpdate(res.data))
            .catch(err => console.error("Error updating todo:", err));
    };

    return (
        <div>
            <h2>To-Do List</h2>
            {todos.length === 0 ? (
                <p>No tasks available.</p>
            ) : (
                <ul>
                    {todos.map(todo => (
                        <li key={todo.id} style={{ marginBottom: '1rem' }}>
                            <input
                                type="checkbox"
                                checked={todo.completed}
                                onChange={() => handleToggleCompleted(todo)}
                            />
                            <strong>{todo.title}</strong> – {todo.completed ? "✅" : "❌"}
                            <br />
                            {todo.description}
                            <br />
                            <em>Due: {todo.dueDate}</em>
                            <br />
                            <button onClick={() => handleDelete(todo.id!)} style={{ marginRight: '0.5rem' }}>
                                Delete
                            </button>
                            <button onClick={() => onUpdate(todo)}>Edit</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default TodoList;
