"use client";

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Todo } from '../types/todo';

interface TodoFormProps {
    onAdd: (newTodo: Todo) => void;
    editingTodo?: Todo | null;
    onUpdateDone?: (updated: Todo) => void;
}

const dayOptions = [
    'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'
];

const TodoForm: React.FC<TodoFormProps> = ({ onAdd, editingTodo, onUpdateDone }) => {
    const [todo, setTodo] = useState<Todo>({
        title: '',
        description: '',
        day: '',
        completed: false,
    });

    useEffect(() => {
        if (editingTodo) setTodo(editingTodo);
    }, [editingTodo]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        const target = e.target as HTMLInputElement;
        const { name, type, value } = target;
        setTodo(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? target.checked : value
        }));
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!todo.title || !todo.day) return alert("Title and day are required!");

        if (editingTodo && onUpdateDone) {
            axios.put<Todo>(`http://localhost:8080/api/todos/${todo.id}`, todo)
                .then(res => {
                    onUpdateDone(res.data);
                    resetForm();
                })
                .catch(err => console.error("Update failed", err));
        } else {
            axios.post<Todo>('http://localhost:8080/api/todos', todo)
                .then(res => {
                    onAdd(res.data);
                    resetForm();
                })
                .catch(err => console.error("Create failed", err));
        }
    };

    const resetForm = () => {
        setTodo({ title: '', description: '', day: '', completed: false });
    };

    return (
        <div style={{ maxWidth: '500px', margin: '0 auto' }}>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '1rem' }}>
                    <label style={{ fontWeight: 'bold' }}>Title</label>
                    <input
                        name="title"
                        value={todo.title}
                        onChange={handleChange}
                        placeholder="Task title"
                        style={{ width: '100%', padding: '0.5rem' }}
                    />
                </div>

                <div style={{ marginBottom: '1rem' }}>
                    <label style={{ fontWeight: 'bold' }}>Description</label>
                    <textarea
                        name="description"
                        value={todo.description}
                        onChange={handleChange}
                        placeholder="Description"
                        style={{ width: '100%', padding: '0.5rem' }}
                    />
                </div>

                <div style={{ marginBottom: '1rem' }}>
                    <label style={{ fontWeight: 'bold' }}>Day</label>
                    <select
                        name="day"
                        value={todo.day}
                        onChange={handleChange}
                        style={{ width: '100%', padding: '0.5rem' }}
                    >
                        <option value="">Select a day</option>
                        {dayOptions.map(day => (
                            <option key={day} value={day}>{day}</option>
                        ))}
                    </select>
                </div>
                <button type="submit" style={{ padding: '0.75rem 1.5rem', background: 'teal', color: 'white', border: 'none' }}>
                    {editingTodo ? 'Update Task' : 'Add Task'}
                </button>
            </form>
        </div>
    );
};

export default TodoForm;
