import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Todo } from '../types/todo';

interface TodoFormProps {
    onAdd: (newTodo: Todo) => void;
    editingTodo?: Todo | null;
    onUpdateDone?: (updated: Todo) => void;
}

const TodoForm: React.FC<TodoFormProps> = ({ onAdd, editingTodo, onUpdateDone }) => {
    const [todo, setTodo] = useState<Todo>({
        title: '',
        description: '',
        dueDate: '',
        completed: false
    });

    useEffect(() => {
        if (editingTodo) {
            setTodo(editingTodo);
        }
    }, [editingTodo]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value, type, checked } = e.target;
        setTodo(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!todo.title || !todo.dueDate) {
            alert("Title and due date are required!");
            return;
        }

        if (editingTodo && onUpdateDone) {
            axios.put<Todo>(`http://localhost:8080/api/todos/${todo.id}`, todo)
                .then(res => {
                    onUpdateDone(res.data);
                    resetForm();
                })
                .catch(err => console.error("Update failed", err));
        } else {
            axios.post<Todo>('http://localhost:8080/api/todos', todo)
                .then(res => {
                    onAdd(res.data);
                    resetForm();
                })
                .catch(err => console.error("Create failed", err));
        }
    };

    const resetForm = () => {
        setTodo({
            title: '',
            description: '',
            dueDate: '',
            completed: false
        });
    };

    return (
        <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
            <h2>{editingTodo ? "Edit Task" : "Add New Task"}</h2>
            <input
                type="text"
                name="title"
                placeholder="Title"
                value={todo.title}
                onChange={handleChange}
                required
            />
            <br />
            <textarea
                name="description"
                placeholder="Description"
                value={todo.description}
                onChange={handleChange}
            />
            <br />
            <input
                type="date"
                name="dueDate"
                value={todo.dueDate}
                onChange={handleChange}
                required
            />
            <br />
            <label>
                <input
                    type="checkbox"
                    name="completed"
                    checked={todo.completed}
                    onChange={handleChange}
                />
                Completed
            </label>
            <br />
            <button type="submit">{editingTodo ? "Update" : "Add Task"}</button>
        </form>
    );
};

export default TodoForm;
