"use client";

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
    Box,
    Button,
    Checkbox,
    Input,
    Stack,
    Text,
    Textarea,
    Portal,
} from '@chakra-ui/react';
import { Select } from '@ark-ui/react/select';
import type { ItemProps } from '@ark-ui/react/select';
import { Todo } from '../types/todo';

interface TodoFormProps {
    onAdd: (newTodo: Todo) => void;
    editingTodo?: Todo | null;
    onUpdateDone?: (updated: Todo) => void;
}

const dayOptions: ItemProps[] = [
    { label: 'Monday', value: 'Monday' },
    { label: 'Tuesday', value: 'Tuesday' },
    { label: 'Wednesday', value: 'Wednesday' },
    { label: 'Thursday', value: 'Thursday' },
    { label: 'Friday', value: 'Friday' },
    { label: 'Saturday', value: 'Saturday' },
    { label: 'Sunday', value: 'Sunday' },
];

const TodoForm: React.FC<TodoFormProps> = ({ onAdd, editingTodo, onUpdateDone }) => {
    const [todo, setTodo] = useState<Todo>({
        title: '',
        description: '',
        day: '',
        completed: false,
    });

    useEffect(() => {
        if (editingTodo) setTodo(editingTodo);
    }, [editingTodo]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const target = e.target as HTMLInputElement;
        const { name, type, value } = target;
        setTodo(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? target.checked : value
        }));
    };

    const handleSelectDay = (value: string[]) => {
        setTodo(prev => ({ ...prev, day: value[0] }));
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!todo.title || !todo.day) return alert("Title and day are required!");

        if (editingTodo && onUpdateDone) {
            axios.put<Todo>(`http://localhost:8080/api/todos/${todo.id}`, todo)
                .then(res => {
                    onUpdateDone(res.data);
                    resetForm();
                })
                .catch(err => console.error("Update failed", err));
        } else {
            axios.post<Todo>('http://localhost:8080/api/todos', todo)
                .then(res => {
                    onAdd(res.data);
                    resetForm();
                })
                .catch(err => console.error("Create failed", err));
        }
    };

    const resetForm = () => {
        setTodo({ title: '', description: '', day: '', completed: false });
    };

    return (
        <Box maxW="md" mx="auto">
            <form onSubmit={handleSubmit}>
                <Stack gap={4}>
                    <Box>
                        <Text fontWeight="bold" mb={1}>Title</Text>
                        <Input name="title" value={todo.title} onChange={handleChange} placeholder="Task title" />
                    </Box>

                    <Box>
                        <Text fontWeight="bold" mb={1}>Description</Text>
                        <Textarea name="description" value={todo.description} onChange={handleChange} placeholder="Description" />
                    </Box>

                    <Box>
                        <Text fontWeight="bold" mb={1}>Day</Text>
                        <Select.Root
                            value={[todo.day]}
                            onValueChange={handleSelectDay}
                            items={dayOptions}
                            selectionMode="single"
                        >
                            <Select.Trigger>
                                <Select.ValueText placeholder="Select day" />
                            </Select.Trigger>
                            <Portal>
                                <Select.Positioner>
                                    <Select.Content>
                                        {dayOptions.map((day) => (
                                            <Select.Item key={day.value} item={day}>
                                                {day.label}
                                                <Select.ItemIndicator />
                                            </Select.Item>
                                        ))}
                                    </Select.Content>
                                </Select.Positioner>
                            </Portal>
                        </Select.Root>
                    </Box>

                    <Button type="submit" colorScheme="teal">
                        {editingTodo ? 'Update Task' : 'Add Task'}
                    </Button>
                </Stack>
            </form>
        </Box>
    );
};

export default TodoForm;
