import React from 'react';
import { Todo } from '../types/Todo';
import axios from 'axios';

interface TodoListProps {
    todos: Todo[];
    onDelete: (id: number) => void;
}

const TodoList: React.FC<TodoListProps> = ({ todos, onDelete }) => {

    const handleDelete = (id: number) => {
        axios.delete(`http://localhost:8080/api/todos/${id}`)
            .then(() => {
                onDelete(id); // Notify parent to remove from state
            })
            .catch(err => console.error("Error deleting todo:", err));
    };

    return (
        <div>
            <h2>To-Do List</h2>
            {todos.length === 0 ? (
                <p>No tasks available.</p>
            ) : (
                <ul>
                    {todos.map(todo => (
                        <li key={todo.id} style={{ marginBottom: '1rem' }}>
                            <strong>{todo.title}</strong> – {todo.completed ? "✅" : "❌"}
                            <br />
                            {todo.description}
                            <br />
                            <em>Due: {todo.dueDate}</em>
                            <br />
                            <button onClick={() => handleDelete(todo.id!)} style={{ marginTop: '0.5rem' }}>
                                Delete
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default TodoList;
