import React, { useState } from 'react';
import WeeklyView from './components/WeeklyView';
import axios from 'axios';

const App = () => {
    const [todos, setTodos] = useState<Todo[]>([]);
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState<Todo>({
        title: '',
        description: '',
        day: '',
        completed: false,
    });

    const handleAddClick = () => setShowForm(true);
    const handleClose = () => {
        setFormData({ title: '', description: '', day: '', completed: false });
        setShowForm(false);
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:8080/api/todos', formData);
            setTodos(prev => [...prev, res.data]);
            handleClose();
        } catch (err) {
            console.error("Create failed", err);
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await axios.delete(`http://localhost:8080/api/todos/${id}`);
            setTodos(prev => prev.filter(todo => todo.id !== id));
        } catch (err) {
            console.error("Delete failed", err);
        }
    };

    return (
        <div>
            <button onClick={handleAddClick} style={{ margin: '1rem', padding: '0.5rem 1rem' }}>+ Add Task</button>

            {showForm && (
                <div style={{
                    position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
                    background: 'rgba(0,0,0,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center'
                }}>
                    <form onSubmit={handleSubmit} style={{
                        background: '#fff', padding: '2rem', borderRadius: '8px', display: 'flex', flexDirection: 'column', gap: '1rem'
                    }}>
                        <h3>Add New Task</h3>
                        <input name="title" value={formData.title} onChange={handleChange} placeholder="Title" required />
                        <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description" />
                        <select name="day" value={formData.day} onChange={handleChange} required>
                            <option value="">Select Day</option>
                            {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map(day => (
                                <option key={day} value={day}>{day}</option>
                            ))}
                        </select>
                        <label>
                            <input type="checkbox" name="completed" checked={formData.completed} onChange={handleChange} />
                            Completed
                        </label>
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <button type="submit">Add</button>
                            <button type="button" onClick={handleClose}>Cancel</button>
                        </div>
                    </form>
                </div>
            )}

            <WeeklyView todos={todos} onDelete={handleDelete} />
        </div>
    );
};

export default App;
