import React from 'react';
import { Todo } from '../types/todo';
import {
    Box,
    Heading,
    SimpleGrid,
    Text,
    Checkbox,
    VStack,
} from '@chakra-ui/react';
import dayjs from 'dayjs';

interface WeeklyViewProps {
    todos: Todo[];
}

const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

const WeeklyView: React.FC<WeeklyViewProps> = ({ todos }) => {
    const getTodosForDay = (day: string) => {
        return todos.filter(todo => dayjs(todo.dueDate).format('dddd') === day);
    };

    return (
        <Box mt={8} px={4}>
            <Heading size="lg" mb={4}>Weekly To Do List</Heading>
            <SimpleGrid columns={[1, 2, 4, 8]} gap={4}>
                {daysOfWeek.map(day => {
                    const dayTodos = getTodosForDay(day);

                    return (
                        <Box key={day} borderWidth="1px" borderRadius="lg" p={4} minH="300px">
                            <Heading size="md" mb={2}>{day}</Heading>
                            <VStack align="start" gap={1}>
                                {dayTodos.map(todo => (
                                    <Checkbox key={todo.id} isChecked={todo.completed} isReadOnly colorScheme="teal">
                                        <Text as={todo.completed ? 's' : undefined}>{todo.title}</Text>
                                    </Checkbox>
                                ))}
                            </VStack>
                        </Box>
                    );
                })}

                <Box borderWidth="1px" borderRadius="lg" p={4} minH="300px">
                    <Heading size="md" mb={2}>Notes</Heading>
                    <VStack align="start" gap={1}>
                        {[...Array(10)].map((_, i) => (
                            <Text key={i} color="gray.400">_____________</Text>
                        ))}
                    </VStack>
                </Box>
            </SimpleGrid>
        </Box>
    );
};

export default WeeklyView;
