import React, { useEffect, useState } from 'react';
import axios from 'axios';
import WeeklyView from './components/WeeklyView';
import { Todo } from './types/todo';

const App = () => {
    const [todos, setTodos] = useState<Todo[]>([]);
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState<Todo>({
        title: '',
        description: '',
        day: '',
        completed: false
    });

    const fetchTodos = async () => {
        try {
            const res = await axios.get<Todo[]>('http://localhost:8080/api/todos');
            setTodos(res.data);
        } catch (err) {
            console.error('Failed to fetch todos', err);
        }
    };

    useEffect(() => {
        fetchTodos();
    }, []);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const target = e.target as HTMLInputElement;
        const { name, type, value, checked } = target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!formData.title || !formData.day) return alert("Title and day are required!");

        try {
            const res = await axios.post<Todo>('http://localhost:8080/api/todos', formData);
            setTodos(prev => [...prev, res.data]);
            setFormData({ title: '', description: '', day: '', completed: false });
            setShowForm(false);
        } catch (err) {
            console.error('Create failed', err);
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await axios.delete(`http://localhost:8080/api/todos/${id}`);
            setTodos(prev => prev.filter(todo => todo.id !== id));
        } catch (err) {
            console.error('Delete failed', err);
        }
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h1>Weekly To-Do List</h1>

            <button onClick={() => setShowForm(true)} style={{ marginBottom: '1rem' }}>+ Add Task</button>

            {showForm && (
                <div style={{ border: '1px solid #ccc', padding: '1rem', marginBottom: '2rem' }}>
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label>Title:</label><br />
                            <input type="text" name="title" value={formData.title} onChange={handleChange} required />
                        </div>

                        <div>
                            <label>Description:</label><br />
                            <textarea name="description" value={formData.description} onChange={handleChange} />
                        </div>

                        <div>
                            <label>Day:</label><br />
                            <select name="day" value={formData.day} onChange={handleChange} required>
                                <option value="">Select a day</option>
                                {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map(day => (
                                    <option key={day} value={day}>{day}</option>
                                ))}
                            </select>
                        </div>

                        <div>
                            <label>
                                <input
                                    type="checkbox"
                                    name="completed"
                                    checked={formData.completed}
                                    onChange={handleChange}
                                />{' '}
                                Completed
                            </label>
                        </div>

                        <button type="submit">Add Task</button>
                        <button type="button" onClick={() => setShowForm(false)} style={{ marginLeft: '1rem' }}>Cancel</button>
                    </form>
                </div>
            )}

            <WeeklyView todos={todos} onDelete={handleDelete} />
        </div>
    );
};

export default App;
