import React, { useEffect, useState } from 'react';
import axios from 'axios';
import WeeklyView from './components/WeeklyView';
import { Todo } from './types/todo';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
    const [todos, setTodos] = useState<Todo[]>([]);
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState<Todo>({
        title: '',
        description: '',
        day: '',
        completed: false
    });

    const fetchTodos = async () => {
        try {
            const res = await axios.get<Todo[]>('http://localhost:8080/api/todos');
            setTodos(res.data);
        } catch (err) {
            console.error('Failed to fetch todos', err);
        }
    };

    useEffect(() => {
        fetchTodos();
    }, []);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
        const target = e.target as HTMLInputElement;
        const { name, type, value, checked } = target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!formData.title || !formData.day) return alert("Title and day are required!");

        try {
            const res = await axios.post<Todo>('http://localhost:8080/api/todos', formData);
            setTodos(prev => [...prev, res.data]);
            setFormData({ title: '', description: '', day: '', completed: false });
            setShowForm(false);
        } catch (err) {
            console.error('Create failed', err);
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await axios.delete(`http://localhost:8080/api/todos/${id}`);
            setTodos(prev => prev.filter(todo => todo.id !== id));
        } catch (err) {
            console.error('Delete failed', err);
        }
    };

    const handleToggleComplete = async (id: number, completed: boolean) => {
        try {
            const todoToUpdate = todos.find(t => t.id === id);
            if (!todoToUpdate) return;

            const res = await axios.put<Todo>(`http://localhost:8080/api/todos/${id}`, {
                ...todoToUpdate,
                completed
            });
            setTodos(prev => prev.map(t => t.id === id ? res.data : t));
        } catch (err) {
            console.error('Update failed', err);
        }
    };

    return (
        <div className="container py-4">
            <h1 className="text-center mb-4 display-5 fw-bold">Weekly To-Do List</h1>

            <div className="text-center mb-4">
                <button
                    className="btn btn-primary px-4 py-2 rounded-pill"
                    onClick={() => setShowForm(true)}
                >
                    + Add Task
                </button>
            </div>

            {showForm && (
                <div className="modal d-block" tabIndex={-1}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Add New Task</h5>
                                <button type="button" className="btn-close" onClick={() => setShowForm(false)}></button>
                            </div>
                            <form onSubmit={handleSubmit}>
                                <div className="modal-body">
                                    <div className="mb-3">
                                        <label className="form-label">Title</label>
                                        <input
                                            type="text"
                                            name="title"
                                            className="form-control"
                                            value={formData.title}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">Description</label>
                                        <textarea
                                            name="description"
                                            className="form-control"
                                            value={formData.description}
                                            onChange={handleChange}
                                        ></textarea>
                                    </div>

                                    <div className="mb-3">
                                        <label className="form-label">Day</label>
                                        <select
                                            name="day"
                                            className="form-select"
                                            value={formData.day}
                                            onChange={handleChange}
                                            required
                                        >
                                            <option value="">Select a day</option>
                                            {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map(day => (
                                                <option key={day} value={day}>{day}</option>
                                            ))}
                                        </select>
                                    </div>

                                    <div className="form-check">
                                        <input
                                            className="form-check-input"
                                            type="checkbox"
                                            name="completed"
                                            checked={formData.completed}
                                            onChange={handleChange}
                                            id="completedCheck"
                                        />
                                        <label className="form-check-label" htmlFor="completedCheck">
                                            Completed
                                        </label>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" onClick={() => setShowForm(false)}>Cancel</button>
                                    <button type="submit" className="btn btn-primary">Add</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            )}

            <WeeklyView todos={todos} onDelete={handleDelete} onToggleComplete={handleToggleComplete} />
        </div>
    );
};

export default App;
