import React, { useState } from 'react';
import axios from 'axios';
import { Todo } from '../types/todo';

interface TodoFormProps {
    onAdd: (newTodo: Todo) => void;
}

const TodoForm: React.FC<TodoFormProps> = ({ onAdd }) => {
    const [todo, setTodo] = useState<Todo>({
        title: '',
        description: '',
        dueDate: '',
        completed: false
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setTodo({ ...todo, [name]: value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!todo.title || !todo.dueDate) {
            alert("Title and due date are required!");
            return;
        }

        axios.post<Todo>('http://localhost:8080/api/todos', todo)
            .then(res => {
                onAdd(res.data); // pass the new todo to parent
                setTodo({ title: '', description: '', dueDate: '', completed: false }); // reset form
            })
            .catch(err => console.error("Error creating todo:", err));
    };

    return (
        <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
            <h2>Add New Task</h2>
            <input
                type="text"
                name="title"
                placeholder="Title"
                value={todo.title}
                onChange={handleChange}
                required
            />
            <br />
            <textarea
                name="description"
                placeholder="Description"
                value={todo.description}
                onChange={handleChange}
            />
            <br />
            <input
                type="date"
                name="dueDate"
                value={todo.dueDate}
                onChange={handleChange}
                required
            />
            <br />
            <button type="submit">Add Task</button>
        </form>
    );
};

export default TodoForm;
