import React from 'react';
import { Todo } from '../types/todo';

interface WeeklyViewProps {
    todos: Todo[];
    onDelete: (id: number) => void;
    onToggleComplete: (id: number, completed: boolean) => void;
}

const daysOfWeek = [
    'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'
];

const today = new Date().toLocaleDateString('en-US', { weekday: 'long' });
const WeeklyView: React.FC<WeeklyViewProps> = ({ todos, onDelete, onToggleComplete }) => {
    const getTodosForDay = (day: string) => {
        return todos.filter(todo => todo.day === day);
    };

    return (
        <div style={{ marginTop: '2rem', padding: '1rem' }}>
            <h2 style={{ fontSize: '1.5rem', marginBottom: '1rem' }}>Today is: {today}</h2>
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
                gap: '1rem'
            }}>
                {daysOfWeek.map(day => {
                    const dayTodos = getTodosForDay(day);

                    return (
                        <div key={day} style={{ border: '1px solid #ccc', borderRadius: '8px', padding: '1rem', minHeight: '300px' }}>
                            <h3 style={{ fontSize: '1.25rem', marginBottom: '0.5rem' }}>{day}</h3>
                            <div style={{ display: 'flex', flexDirection: 'column', gap: '5rem' }}>
                                {dayTodos.map(todo => (
                                    <div key={todo.id} style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                                        <label style={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
                                            <input
                                                type="checkbox"
                                                checked={todo.completed}
                                                onChange={() => onToggleComplete(todo.id!, !todo.completed)}
                                                style={{ marginRight: '0.5rem' }}
                                            />
                                            <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>{todo.title}</span>
                                        </label>
                                        <button
                                            onClick={() => onDelete(todo.id!)}
                                            style={{ background: 'transparent', border: 'none', color: 'red', fontWeight: 'bold', cursor: 'pointer' }}
                                        >
                                            &times;
                                        </button>
                                    </div>
                                ))}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default WeeklyView;
