import React from 'react';
import { Todo } from '../types/todo';

interface WeeklyViewProps {
    todos: Todo[];
}

const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

const WeeklyView: React.FC<WeeklyViewProps> = ({ todos }) => {
    const getTodosForDay = (day: string) => {
        return todos.filter(todo => todo.day === day);
    };

    return (
        <div style={{ marginTop: '2rem', padding: '1rem' }}>
            <h2 style={{ fontSize: '1.5rem', marginBottom: '1rem' }}>Weekly To Do List</h2>
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
                gap: '1rem'
            }}>
                {daysOfWeek.map(day => {
                    const dayTodos = getTodosForDay(day);

                    return (
                        <div key={day} style={{ border: '1px solid #ccc', borderRadius: '8px', padding: '1rem', minHeight: '300px' }}>
                            <h3 style={{ fontSize: '1.25rem', marginBottom: '0.5rem' }}>{day}</h3>
                            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                                {dayTodos.map(todo => (
                                    <label key={todo.id} style={{ display: 'flex', alignItems: 'center' }}>
                                        <input
                                            type="checkbox"
                                            checked={todo.completed}
                                            disabled
                                            style={{ marginRight: '0.5rem' }}
                                        />
                                        <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>{todo.title}</span>
                                    </label>
                                ))}
                            </div>
                        </div>
                    );
                })}

                <div style={{ border: '1px solid #ccc', borderRadius: '8px', padding: '1rem', minHeight: '300px' }}>
                    <h3 style={{ fontSize: '1.25rem', marginBottom: '0.5rem' }}>Notes</h3>
                    <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
                        {[...Array(10)].map((_, i) => (
                            <span key={i} style={{ color: '#999' }}>_____________</span>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default WeeklyView;
